function prompt() {
   write-host "[" -nonewline -foregroundcolor "darkblue"
   write-host $(get-date -format "ddd MMM d h:mtt") -nonewline -foregroundcolor "darkgray"
   write-host "][" -nonewline -foregroundcolor "darkblue"
   write-host $(get-location) -nonewline -foregroundcolor "darkgray"
   write-host "] >" -nonewline -foregroundcolor "darkblue"
   return " "
}